import {createSlice} from "@reduxjs/toolkit";

const showCartUISlice = createSlice({
    name: "showCart",
    initialState: {showCartItems: false},
    reducers: {
        showHideCart(state) {
            state.showCartItems = !state.showCartItems
        }
    }
})

export const uiActions = showCartUISlice.actions

export default showCartUISlice;
